package es.eoi.ejercicio1;

public class CuadradoPedro {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("3X3");
		System.out.println();
		for (int j = 1; j <=3; j++) {
		        for (int i = 1; i<=3; i++){		        	 
		             System.out.print("#");		         
		            
		        }
		        System.out.println();
		}
		
		System.out.println();
		System.out.println("4X4");
		System.out.println();
		for (int j = 1; j <=4; j++) {
		        for (int i = 1; i<=4; i++){		        	 
		             System.out.print("#");		         
		            
		        }
		        System.out.println();
		}
		
		System.out.println();
		System.out.println("10X10");
		System.out.println();
		for (int j = 1; j <=10; j++) {
		        for (int i = 1; i<=10; i++){		        	 
		             System.out.print("@");		         
		            
		        }
		        System.out.println();
		}

	}
}
