package es.eoi.ejercicio1;

public class MultiplicacionesPedro {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		for (int j = 0; j <=10; j++) {
		System.out.println("Tabla del " + j);
	        for(int i = 0; i<=10; i++){
	        	 
	             System.out.println(j + " * " + i + " = " + j*i);
	            
	        }
		}
	}

}
